import { createStore } from "vuex";
// crypto.randomUUID()
export default createStore({
  state: {
    posts: ["name", "surname"],
  },

  getters: {},

  mutations: {
    addPost(state, payload) {
      state.posts.push(payload);
    },
  },

  actions: {
    addPost({ commit }, payload) {
      commit("name", payload);
      commit("surname", payload);
    },
  },
});
