import { createStore } from "vuex";

export default createStore({
  state: {
    posts: [],
    comments: [],
  },

  getters: {},

  mutations: {
    addPost(state, post) {
      state.posts.push(post);
    },
    addComment(state, post) {
      state.comments.push(post);
    },
  },

  actions: {
    addPost({ commit }, post) {
      commit("addPost", post);
    },
    addComment({ commit }, post) {
      commit("addComment", post);
    },
  },
});
